from microbot import *

def on_forever():
    def on_received_string(receivedString):
        if f'ping-{str(bot_id)}' in receivedString:
            radio.send_string(f'pong-{str(bot_id)}')
            print('[microbot] sent alive packet')
        if f'pwn-{str(bot_id)}' in receivedString:
            basic.show_string("pwned")
            while True:
                basic.show_icon(IconNames.SKULL)
        if f'kill-{str(bot_id)}' in receivedString:
            music.play_tone(Note.C, music.beat())
            crashbuf = "" # kind of weird, it's allocating more memory than on the actual device
            for i in range(100000):
                crashbuf += ""
    radio.on_received_string(on_received_string)
    
    
radio.set_group(231) # running on group 231 / port 231
radio.set_transmit_power(7) # set high transmit power.
radio.set_transmit_serial_number(True) # send serial number to server :)

bot_id = randint(1, 100000000)
radio.send_string(f'connect-{str(bot_id)}')
print('[microbot] sent connect packet')

basic.forever(on_forever) # create thread
